mapper.select:
SELECT ID, PASSWORD, NAME, NICKNAME, PHONE, EMAIL,
TO_CHAR(REGIST_DATE, 'YYYY-MM-DD hh24:mi') AS REGIST_DATE, FLAG_ADMIN	
FROM (
	SELECT ROWNUM AS RNUM, ID, PASSWORD, NAME, NICKNAME, PHONE, EMAIL,
	REGIST_DATE, FLAG_ADMIN	FROM (
		SELECT * FROM MEMBER 
		WHERE ID<>'admin1' AND ID<>#{adminId}
		ORDER BY FLAG_ADMIN, REGIST_DATE
	)
) WHERE RNUM>=#{startRow} AND RNUM<=#{endRow}

mapper.update:
UPDATE MEMBER
SET
	FLAG_ADMIN=#{flag_admin}
WHERE
	ID = #{id}
